$(document).ready(function(){$("#contact-form").submit(function(e){e.preventDefault();var s=$(this);$.ajax({type:"POST",url:s.attr("action"),data:s.serialize(),success:function(e){$("#success-message").show(),s[0].reset()},error:function(){$("#error-message").show(),$("#success-message").hide()}})}),$("#submit-btn").click(function(){$("#success-message").hide(),$("#error-message").hide()})});